index/style.css:
* {
	box-sizing: border-box;
	outline: 0;
	-ms-overflow-style: none;
	scrollbar-width: none;
}



html {
	height: 100%;
}

@font-face {
	font-family: 'HarmonyOS Sans';src: url('HarmonyOS_Sans_Black.woff2') format('woff2'), url('HarmonyOS_Sans_Black.woff') format('woff');font-weight: 900;font-style: normal;font-display: swap;
}

body {
	max-width: 100%;
	overflow-x: hidden;
	margin: 0;
	segoe ui, Arial, sans-serif;
	background-color: #fff;
	cursor: default;
	-webkit-tap-highlight-color: transparent;
	-webkit-user-select: none;
	user-select: none;
}

body:lang(en) {
	font-family:  'HarmonyOS Sans';src: url('HarmonyOS_Sans_Black.woff2') format('woff2'), url('HarmonyOS_Sans_Black.woff') format('woff');font-weight: 900;font-style: normal;font-display: swap;
}

@font-face {
	font-display: swap;
	font-family: 'HarmonyOS Sans';src: url('HarmonyOS_Sans_Black.woff2') format('woff2'), url('HarmonyOS_Sans_Black.woff') format('woff');font-weight: 900;font-style: normal;font-display: swap;
}

i {
	font-family: 'HarmonyOS Sans';src: url('HarmonyOS_Sans_Black.woff2') format('woff2'), url('HarmonyOS_Sans_Black.woff') format('woff');font-weight: 900;font-style: normal;font-display: swap;
	font-size: 22px;
	font-style: normal;
	-webkit-font-smoothing: antialiased;
	-webkit-text-stroke-width: 0;
	-moz-osx-font-smoothing: grayscale;
}

@keyframes fadeIn {
	0% {
		opacity: 0;
	}

	100% {
		opacity: 1;
	}
}

a {
	color: inherit;
	text-decoration: none;
	transition: 0.25s;
}

#followUsContainer, #topContainer, .detailContainer, footer, header {
	max-width: 1500px;
	margin: 0 auto;
	transition: 1s var(--cubic-bezier);
}

header {
	padding: 20px 100px;
}

#topLogo {
	height: 50px;
	margin-left: 3px;
	transition: 0.5s;
}

#topContainer {
	display: flex;
	align-items: center;
	justify-content: space-between;
	width: 100%;
	height: calc(100vh - 200px);
	max-height: 1000px;
	padding: 0 100px;
	background-attachment:fixed; 
	background-size: 100%;
}

#topContainerCN {
	display: flex;
	align-items: center;
	justify-content: space-between;
	width: 100%;
	height: calc(100vh - 200px);
	max-height: 1000px;
	padding: 0 100px;
	background-image: url(../index/win.jpg);
	background-attachment:fixed; 
	background-size: 100%;
}

.topSloganContainer {
	flex-shrink: 0;
	transition: 0.25s;
}

h1 {
	margin-top: 0;
	color: #000;
	font-size: 40px;
	animation: fadeIn 0.5s ease-in 0.3s;
	animation-fill-mode: both;
}
.h1CN {
	margin-top: 0;
	color: #fff;
	font-size: 40px;
	animation: fadeIn 0.5s ease-in 0.3s;
	animation-fill-mode: both;
}

#topSloganAutoWrap {
	display: none;
}

#topSloganContainer p {
	color: rgba(0, 0, 0, 0.4);
	font-size: 24px;
	animation: fadeIn 0.5s ease-in 0.5s;
	animation-fill-mode: both;
}

#mainBtnContainer {
	margin-top: 80px;
	font-size: 0;
}

.mainBtn {
	padding: 15px 5px;
	border: none;
	border-radius: 10px;
	font-family: 	'HarmonyOS Sans';src: url('HarmonyOS_Sans_Black.woff2') format('woff2'), url('HarmonyOS_Sans_Black.woff') format('woff');font-weight: 900;font-style: normal;font-display: swap;
	font-size: 18px;
	overflow: hidden;
	text-align: center;
	white-space: nowrap;
	text-overflow: ellipsis;
	cursor: pointer;
	transition: 0.25s;
}

.btnStart {
	width: 50%;
	color: #fff;
	background-color: #0077c0;
	animation: fadeIn 0.5s ease-in 0.7s;
	animation-fill-mode: both;
}

.btnStart:active {
	filter: none;
	transform: scale(0.95);
	background-color: var(--theme-color-active1);
}

.btnform {
	margin-left: 10px;
	width: calc(50% - 10px);
	border: 1px solid rgba(0, 0, 0, 0.1);
	color: #000;
	background-color: #fff;
	animation: fadeIn 0.5s ease-in 0.9s;
	animation-fill-mode: both;
}

.btnform:active {
	transform: scale(0.92);
	box-shadow: none;
}

.smalla{
	color:#95a5a6;
	animation: fadeIn 0.5s ease-in 0.7s;
	animation-fill-mode: both;
}

#topImgContainer {
	padding-left: 100px;
}

#topImg {
	opacity: 0;
	transform: translateY(10vh);
	max-width: 900px;
	width: 100%;
	border-radius: 10px;
	box-shadow: 0 20px 60px rgba(0, 0, 0, 0.25);
	transition: 0.6s;
}

#topImg.loaded {
	opacity: 1;
	transform: none;
}

#featuresContainer {
	margin-top: 110px;
	background-color: white;
	text-align: center;
}

#featuresContainer h2 {
	text-indent: 20px;
}

#featuresContainer p {
	color: rgba(0, 0, 0, 0.8);
}

h1 {
	margin: 10px 0;
	font-size: 36px;
}

h3 {
	color: #025eb2;
	letter-spacing: 20px;
	text-indent: 20px;
}

:lang(en-us) h3 {
	letter-spacing: 15px;
	text-indent: 15px;
}

#featuresList {
	display: flex;
	align-items: center;
	flex-wrap: wrap;
	max-width: 1300px;
	margin: 30px auto 0;
}

#featuresList i {
	padding-right: 15px;
	color: #0017c0;
	font-size: 32px;
}

#featuresList p {
	margin: 6px 0 0;
	font-size: 14px;
}

.featureItem {
	display: flex;
	width: calc(33% - 10px);
	padding: 10px 30px;
	margin: 5px;
	border-radius: 10px;
	background-color: #ebf5ff;
	text-align: left;
	min-height: 15px;
}

.featureItem div:first-child {
	max-width: 47px;
}

h4 {
	margin: 0;
	font-size: 20px;
}

.detailContainer {
	display: flex;
	align-items: center;
	justify-content: space-between;
	padding: 70px 100px;
}

.detailContainer p {
	line-height: 26px;
}

.detailContainer img {
	height: 400px;
	padding-left: 70px;
}

.detailContainer:nth-of-type(even) {
	flex-direction: row-reverse;
}

.detailContainer:nth-of-type(even) img {
	padding-left: 0;
	padding-right: 70px;
}

#detailImgDark {
	height: 460px;
}

#startNow {
	padding: 70px 100px;
	background-color: var(--theme-color);
	background: linear-gradient(to right, #008ac0, #5b96e3);
	color: #fff;
	text-align: center;
}

#startOptionsList {
	margin-top: 35px;
	font-size: 0;
}

.bottomBtn {
	display: inline-flex;
	flex-direction: column;
	width: 180px;
	margin: 3px;
	padding: 15px 0;
	border-radius: 5px;
	background-color: #fff;
	color: #000;
	cursor: pointer;
	transition: 0.25s;
}

.bottomBtn:hover {
	transform: scale(1.03);
	filter: brightness(1.1);
}

.bottomBtn:active {
	transform: scale(0.95);
	filter: none;
	opacity: 0.6;
}

.bottomBtn div {
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 16px;
}

.bottomBtn img {
	width: 24px;
	margin-right: 10px;
}

.bottomBtn span {
	margin-top: 5px;
	font-size: small;
}

.menu {
	opacity: 0;
	transform: scale(0.5);
	transform-origin: 0 0;
	position: absolute;
	min-width: 200px;
	padding: 5px;
	border-radius: 10px;
	background-color: #fff;
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.25);
	color: #000;
	font-size: 16px;
	text-align: left;
	transition: 0.25s;
}

.menu div {
	padding: 6px 20px;
	border-radius: 5px;
	transition: 0.25s;
}

.menu div:hover {
	background-color: rgba(0, 0, 0, 0.05);
}

.menu div:active {
	background-color: rgba(0, 0, 0, 0.1);
}

.menuDividingLine {
	display: block;
	margin: 5px 0 5px -5px;
	width: calc(100% + 10px);
	height: 1px;
	background-color: rgba(0, 0, 0, 0.1);
}

#followUsContainer {
	display: flex;
	align-items: center;
	flex-wrap: wrap;
	padding: 50px 100px;
}

.followUsItem {
	margin: 10px 0;
}

.followUsItem:not(:last-child) {
	margin-right: 100px;
}

.followUsItem:first-child p {
	margin: 5px 0;
	color: rgba(0, 0, 0, 0.6);
	font-size: 14px;
}

.followUsItem a {
	display: flex;
	align-items: center;
}

.followUsItem a:hover {
	transform: scale(1.05);
}

.followUsItem a:active {
	transform: scale(0.95);
}

.followUsItem a div:first-child {
	display: flex;
	align-items: center;
	justify-content: center;
	margin-right: 12px;
	width: 36px;
	height: 36px;
	border-radius: 10px;
}

.followUsItem a i {
	color: #fff;
	font-size: 24px;
}

.followUsItem a p {
	margin: 0 0 3px;
	color: #000;
}

.followUsItem a span {
	color: rgba(0, 0, 0, 0.6);
	font-size: 14px;
}

#iconWeibo {
	background-color: #ce092e;
}

#iconBili {
	background-color: #0f98cb;
}

.separator {
	height: 1px;
	background-color: rgba(0, 0, 0, 0.05);
}

footer {
	display: flex;
	align-items: center;
	justify-content: space-between;
	flex-wrap: wrap;
	padding: 80px 100px;
	color: rgba(0, 0, 0, 0.6);
}

footer a {
	color: var(--theme-color);
}

footer a:hover {
	filter: brightness(1.1);
}

footer a:active {
	filter: brightness(0.9) !important;
}

footer div:first-child {
	font-size: 14px;
}

footer div:last-child a {
	color: rgba(0, 0, 0, 0.6);
}

footer div:last-child a:hover {
	filter: brightness(1.2);
}

footer i {
	color: #eb2a43;
	font-size: 18px;
}

@supports (-webkit-backdrop-filter: blur(10px)) or (backdrop-filter: blur(10px)) {
	.menu {
		background-color: rgba(255, 255, 255, 0.8);
		-webkit-backdrop-filter: blur(20px);
		backdrop-filter: blur(20px);
	}
}

@media screen and (max-width: 1366px) {
	#topLogo {
		margin-left: 0;
	}

	h1 {
		/* font-size: 6px; */
	}

	#topSloganContainer p {
		font-size: 20px;
	}
}

@media screen and (max-width: 1150px) {
	#topContainer {
		padding-right: 0;
		overflow-x: hidden;
	}

	#topImg {
		width: auto;
		height: 60vh;
	}
}

@media screen and (max-width: 1050px) {
	.featureItem {
		width: calc(50% - 10px);
	}

	.detailContainer {
		flex-direction: column !important;
		padding-top: 50px;
		text-align: center;
	}

	.detailContainer img {
		margin-top: 30px;
		padding: 0 !important;
	}
}

@media screen and (max-width: 960px) {
	#featuresContainer, #followUsContainer, #startNow, #topContainer, #topImgContainer, .detailContainer, footer, header {
		padding-left: 60px;
		padding-right: 60px;
	}
}

@media screen and (max-width: 860px) {
	#topContainer {
		flex-direction: column;
		align-items: flex-start;
		height: auto;
		max-height: unset;
		padding: 80px 60px;
	}

	#topImgContainer {
		padding: 120px 0 0;
	}

	#topImg {
		width: 100%;
		height: auto;
	}

	#featuresContainer {
		margin-top: 0;
	}

	.detailContainer img {
		width: 70%;
		height: auto !important;
	}
}

@media screen and (max-width: 680px) {
	#topSloganContainer {
		width: 100%;
	}

	#mainBtnContainer {
		margin-top: 35px;
	}

	.mainBtn, h4 {
		font-size: 16px;
	}

	.featureItem {
		width: 100%;
		margin: 5px 0;
		padding: 15px 20px;
	}

	#featuresList p {
		margin-top: 3px;
	}

	#featuresContainer p, #featuresList p, #startNow p, .detailContainer p, .followUsItem a p {
		font-size: 14px;
	}

	footer {
		font-size: small;
	}
}

@media screen and (max-width: 480px) {
	.detailContainer img {
		width: 100%;
	}

	.bottomBtn {
		width: 100%;
		margin: 3px 0;
	}

	.followUsItem {
		width: 100%;
		margin-right: 0 !important;
	}

	footer div {
		width: 100%;
		margin: 3px 0;
	}
}

@media screen and (max-width: 450px) {
	#topSlogan {
		display: none;
	}

	#topSloganAutoWrap {
		display: block;
	}
}

@media screen and (max-width: 420px) {
	#featuresContainer, #followUsContainer, #startNow, #topContainer, .detailContainer, footer, header {
		padding-left: 30px;
		padding-right: 30px;
	}
}

#featuresContainertitle{
	margin-right: 100px;
	margin-top: 0;
    color: black;
    font-size: 36px;
    animation: fadeIn 0.5s ease-in 0.3s;
    animation-fill-mode: both;
}

#up{
	float:left;
	width: 50px;
	height: 50px;
	position: fixed;
}

